import { Carousel3d, Slide } from 'vue-carousel-3d'
import { gsap } from 'gsap'
import ScrollTrigger from 'gsap/ScrollTrigger'
import { getDatabase, ref, onValue } from 'firebase/database'
gsap.registerPlugin(ScrollTrigger)

export default {
  name: 'TheHome',
  components: {
    VueSlickCarousel,
    Carousel3d,
    Slide,
    LazyYoutubeVideo
  },
  data () {
    return {
      settingSliderNfts: {
        space: 400,
        display: 5,
        height: 403
      },
      menuAnimate: 0,
      registerLink: {
        link: ''
      },
      footerMenu: {
        contact: '',
        litepaper: ''
      },
      videoSettings: {
        videoUrl: '',
        placeholderUrl: ''
      },
      social: {
        discord: '',
        telegram: '',
        twitter: '',
        facebook: '',
        youTube: '',
        social: ''
      },
      sliderAbout: {
        infinite: true,
        arrows: false,
        slidesToShow: 5,
        slidesToScroll: 1,
        speed: 500,
        swipeToSlide: true,
        responsive: [
          {
            breakpoint: 1440,
            settings: {
              slidesToShow: 5
            }
          },
          {
            breakpoint: 992,
            settings: {
              slidesToShow: 4
            }
          },
          {
            breakpoint: 767,
            settings: {
              slidesToShow: 3
            }
          }
        ]
      },
      sliderItems: [],
      mainMenu: [
        {
          name: 'ABOUT',
          link: '#about'
        },
        {
          name: 'NFTs',
          link: '#nfts'
        },
        {
          name: 'TEAM',
          link: '#team'
        },
        {
          name: 'PARTNERS',
          link: '#partners'
        }
      ],
      partners: [],
      team: [],
      about: [],
      checkApi: 0
