        y: -200,
        autoAlpha: 0
      }, {
        y: 0,
        autoAlpha: 1,
        duration: 0.6
      }, '-=0.4')
    },
    createAnimations: function () {
      setTimeout(() => {
        gsap.timeline({
          scrollTrigger: {
            trigger: '.section-about .container',
            start: 'top 20%',
            end: 'bottom 25%',
            onEnter: () => {
              this.menuAnimate = 0
            },
            onEnterBack: () => {
              this.menuAnimate = 0
            }
          }
        })
        gsap.timeline({
          scrollTrigger: {
            trigger: '.section-nfts .container',
            start: 'top 35%',
            end: 'bottom 20%',
            onEnter: () => {
              this.menuAnimate = 1
            },
            onEnterBack: () => {
              this.menuAnimate = 1
            }
          }
        })
        gsap.timeline({
          scrollTrigger: {
            trigger: '.section-team .container',
            start: 'top 20%',
            end: 'bottom 20%',
            onEnter: () => {
              this.menuAnimate = 2
            },
            onEnterBack: () => {
              this.menuAnimate = 2
            }
          }
        })
        gsap.timeline({
          scrollTrigger: {
            trigger: '.section-partners',
            start: 'top 20%',
            end: 'bottom 20%',
            onEnter: () => {
              this.menuAnimate = 3
            },
            onEnterBack: () => {
              this.menuAnimate = 3
            }
          }
        })
      }, 1000)
      const heroTl = gsap.timeline({
        scrollTrigger: {
          trigger: '.section-firstScrean',
          start: 'top 15%',
          end: 'bottom 10%'
