        autoAlpha: 0
      }, {
        y: 0,
        x: 0,
        autoAlpha: 1,
        duration: 0.6
      }, '-=0.4')

      heroTl.fromTo('.section-firstScrean__heroes .hero-3', {
        y: 130,
        x: 100,
        autoAlpha: 0
      }, {
        y: 0,
        x: 0,
        autoAlpha: 1,
        duration: 0.6
      }, '-=0.4')

      const aboutTl = gsap.timeline({
        scrollTrigger: {
          trigger: '.section-about',
          start: 'top 35%',
          end: 'bottom 10%'
        }
      })

      aboutTl.fromTo('.section-about__user img', {
        autoAlpha: 0,
        x: -200
      }, {
        duration: 0.55,
        x: 0,
        autoAlpha: 1
      })

      const nftTl = gsap.timeline({
        scrollTrigger: {
          trigger: '.section-nfts',
          start: 'top 45%'
        }
      })

      nftTl.fromTo('.section-nfts__image img', {
        autoAlpha: 0,
        x: 250
      }, {
        duration: 0.65,
        x: 0,
        autoAlpha: 1
      })

      nftTl.fromTo('.section-nfts__testSlider', {
        autoAlpha: 0,
        y: -150
      }, {
        y: 0,
        delay: 1,
        duration: 0.35,
        autoAlpha: 1,
        scrollTrigger: {
          trigger: '.section-nfts',
          start: 'top 100%'
        }
      })
    }
  }
}
